parameters.jsCode:
const keywords = [
  "data analyst",
  "business intelligence",
  "power bi",
  "data scientist",
  "machine learning",
  "sql",
  "python",
  "dashboard"
];

const jobs = items[0].json.jobs;

const filteredJobs = jobs.filter(job => {
  const title = job.title?.toLowerCase() || "";
  const description = job.description?.toLowerCase() || "";
  const tags = (job.tags || []).join(" ").toLowerCase();

  return keywords.some(keyword =>
    title.includes(keyword) ||
    description.includes(keyword) ||
    tags.includes(keyword)
  );
});

if (filteredJobs.length === 0) {
  return [
    {
      json: {
        message: "No matching jobs found"
      }
    }
  ];
}

return filteredJobs.slice(0, 3).map(job => ({
  json: {
    title: job.title,
    company: job.company_name,
    url: job.url,
    tags: job.tags,
    description: job.description   // âœ… THIS WAS MISSING
  }
}));

// Extract the AI text response
const aiText = $json.output[0].content[0].text;

// Parse JSON safely
let parsed;
try {
  parsed = JSON.parse(aiText);
} catch (e) {
  parsed = { error: "Failed to parse AI output" };
}

return [
  {
    json: {
      title: $json.title,
      company: $json.company,
      url: $json.url,
      skills: parsed.skills || [],
      tools: parsed.tools || [],
      keywords: parsed.keywords || [],
      experience_level: parsed.experience_level || ""
    }
  }
];

